import '../../styles/Main.css';
import '../../styles/Owner.css';
import noPhoto from '../../img/no-photo.png';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function EditPremiseData() {
    const location = useLocation();
    const segments = location.pathname.split('/');
    const id = segments[segments.length - 1];

    const [errorText, setErrorText] = useState('');
    const [photo, setPhoto] = useState(noPhoto);
    const [photo2, setPhoto2] = useState(null);
    const [typePremise, setTypePremise] = useState('Жилые помещение');
    const [name, setName] = useState('');
    const [adress, setAdress] = useState('');
    const [pool, setPool] = useState('');
    const [billiards, setBilliards] = useState('');
    const [furniture, setFurniture] = useState('');
    const [repair, setRepair] = useState('');
    const [area, setArea] = useState('');
    const [price, setPrice] = useState('');
    
   
    useEffect(() => {

        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/premise/element?id=${id}`);
                console.log(response.data);
                if(response.data.Image === 'null') {response.data.Image = noPhoto}
                const premise = response.data;

                setPhoto(premise.Image);
                setName(premise.Name);
                setPrice(premise.Price);
                setAdress(premise.Address);
                setPool(premise.DescriptionCharacteristicRef.find(des => des.Characteristic.Name === 'Бассейн').Description);
                setBilliards(premise.DescriptionCharacteristicRef.find(des => des.Characteristic.Name === 'Бильярд').Description);
                setFurniture(premise.DescriptionCharacteristicRef.find(des => des.Characteristic.Name === 'Мебель').Description);
