    const add = async (e) => {
        if(price === '' || name === '' || adress === '') {
            setErrorText("Название, адресс и цена должны быть заполнены!"); 
            return;
        }
        
        console.log('ракета');
        try {
            const user = JSON.parse(localStorage.getItem('user'))
            let type;
            let descriptions = [];

            switch (typePremise) {
                case 'Жилые помещение': {
                    type = 3;
                    descriptions = [
                        {char: 'Площадь', des: area },
                        {char: 'Мебель', des: furniture },
                        {char: 'Бассейн', des: pool },
                        {char: 'Бильярд', des: billiards },
                        {char: 'Ремонт', des: repair },
                    ];
                    break;
                }
                case 'Пустые помещения': {
                    type = 5;
                    descriptions = [
                        {char: 'Площадь', des: area },
                        {char: 'Ремонт', des: repair },
                    ];
                    break;
                }
                case 'Комерчиские помещения': {
                    type = 4;
                    descriptions = [
                        {char: 'Площадь', des: area },
                        {char: 'Мебель', des: furniture },
                        {char: 'Ремонт', des: repair },
                    ];
                    break;
                }
                case 'Офисы': {
                    type = 1;
                    descriptions = [
                        {char: 'Площадь', des: area },
                        {char: 'Мебель', des: furniture },
                        {char: 'Ремонт', des: repair },
                    ];
                    break;
                }
                case 'Склады': {
                    type = 2;
                    descriptions = [
                        {char: 'Площадь', des: area },
                        {char: 'Ремонт', des: repair },
                    ];
