                }
            }
              const formData = new FormData();
              formData.append('name', name);
              formData.append('adress', adress);
              formData.append('price', price);
              formData.append('owner', user.ID);
              formData.append('type', type);
              formData.append('photo', photo2);

              formData.append('descriptions', JSON.stringify(descriptions));

              const response = await axios.post('http://localhost:3001/premise/add', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
              });

              console.log(response.data.user);
              

              window.location.href = '/main';
        } catch (error) {
              console.error('Ошибка при изменении данных пользователя:', error);
        }
  } ;

    const activeZiliuyTypePremise = {
        backgroundColor: typePremise === 'Жилые помещение' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Жилые помещение' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

