        backgroundColor: typePremise === 'Комерчиские помещения' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Комерчиские помещения' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    const changePhoto = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        
        setPhoto2(file);
        reader.onloadend = () => {
              setPhoto(reader.result);
        };
    
        if (file) {
          reader.readAsDataURL(file);
        }
  };
    

	return (
        <div className="premiseData">
            <form className='premisePhotoController'>
                <img src={photo} alt="Uploaded"/>
                <input type="file" accept="image/*" className='loadPremisePhoto' onChange={changePhoto}/>
                <input type="text" className='premiseName' placeholder='Название помещения' value={name} onChange={(e) => setName(e.target.value)}/>
                <input type="text" className='premiseName' placeholder='адресс' value={adress} onChange={(e) => setAdress(e.target.value)}/>
                <div className='changeCharacteristicList'>
                    <button type="button" style={activeZiliuyTypePremise} onClick={() => setTypePremise("Жилые помещение")}>Жилые помещение</button>
