                        <input type='number' placeholder='Площадь' value={area} onChange={(e) => setArea(e.target.value)}/>
                    </div>
                    <input type='text' placeholder='Ремонт' value={repair} onChange={(e) => setRepair(e.target.value)}/>
                </form>
            )}

            {typePremise === 'Комерчиские помещения' && (
                <form className="premiseCharacteristics">
                    <div className="area-price">
                        <input type='number' placeholder='Цена' value={price} onChange={(e) => setPrice(e.target.value)}/>
                        <input type='number' placeholder='Площадь' value={area} onChange={(e) => setArea(e.target.value)}/>
                    </div>        
                    <input type='text' placeholder='Ремонт' value={repair} onChange={(e) => setRepair(e.target.value)}/>
