import '../../styles/Main.css';
import '../../styles/Rentals.css';
import '../../styles/Authorization.css';
import noPhoto from '../../img/no-photo.png';
import deleteButton from '../../img/delete.svg';
import axios from 'axios';
import { useState, useEffect } from 'react';
const accessToken = localStorage.getItem("accessToken");
const refreshToken = localStorage.getItem("refreshToken");

function AnnouncementList({ announcements }) {
    const [announcementsList, setAnnouncementsListList] = useState([]);

    useEffect(() => {
        setAnnouncementsListList(announcements);
    }, [announcements]);

	return (
        <div className="userList" style={{paddingTop: '40px'}}>
            <form onSubmit={handleSubmit} className="authForm">
                <input type="text" name="login" style={{marginTop: '40px'}} placeholder="Логин"/>
                <input type="password" name="password" style={{marginTop: '40px'}} placeholder="Пароль" />
                <p id="errorMessage" style={{marginTop: '15px'}}>{errorText} </p>
                <button type="submit" style={{marginTop: '110px'}}>Войти</button>
            </form>

            <div style={{paddingTop: '30px'}}>
                {Array.isArray(announcementsList) && usersList.map((announcement) => (
                    <a href={`/edit_user/${user.ID}`} className="rental">
                    {user.Photo !== 'data:image/png;base64,null' ? ( <img src={user.Photo} alt="" style={{width: '190px'}}/>) : (<img src={noPhoto} alt="Placeholder"/>)}
                        <div className="name_price">
                            <div>{announcements}</div>
                        </div>
                        <button style={{marginLeft: '100px'}} className='deleteButton' onClick={(e) => { e.preventDefault(); deletePremise(user.ID); }}><img src={deleteButton}/></button>
                    </a>
                ))}
            </div>
        </div>
  	);
}

export default AnnouncementList;
