import '../../styles/Main.css';
import '../../styles/Rentals.css';
import deleteButton from '../../img/delete.svg';
import axios from 'axios';
import { useState, useEffect } from 'react';



function OwnershipList({ premises }) {
    const [premisesList, setPremisesList] = useState(premises);

    useEffect(() => {
        const fetchData = async () => {
            const user  = JSON.parse(localStorage.getItem('user'));
            setUser(user);

            try {
                const response = await axios.get(`http://localhost:3001/user/photo?login=${user .Login}`, {
                    responseType: 'blob' // Установить responseType на 'blob' для получения данных в виде Blob
                });

                const reader = new FileReader();
                console.log(response.data);
                reader.readAsDataURL(response.data); // Прочитать данные в виде Data URL

                reader.onload = () => {
                const photoUrl = reader.result; // Получить Data URL изображения
                setAva(photoUrl);
                };
              } catch (error) {
                console.error('Error fetching user photo:', error);
              }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);
    
    const deletePremise = async (id) => {
        try {
            // Отправляем запрос на удаление объекта на сервер
            await axios.delete(`http://localhost:3001/premise/remove/${id}`);

            // Обновляем список помещений, удаляя удаленный объект
            setPremisesList(prevList => prevList.filter(premise => premise.ID !== id));
        } catch (error) {
            console.error("Ошибка при удалении помещения:", error);
        }
    };

	return (
        <div className="userList">
            <a href='/add_premise' className='addPremiseButton'><p>+</p></a>

            {Array.isArray(premisesList) && premisesList.map((premise) => (
                <a href={`/premise/${premise.ID}`} className="rental">
                    <img src={premise.Image}/>
                    <div className="name_price">
                        <div>{premise.Name}</div>
                        <div style={{marginTop: '25px'}}>{premise.Price} BYN</div>
                    </div>
                    <button className='deleteButton' onClick={(e) => { e.preventDefault(); deletePremise(premise.ID); }}><img src={deleteButton}/></button>
                </a>
            ))}
            
        </div>
  	);
}

export default OwnershipList;
