import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation, Navigate  } from "react-router-dom";
import { useEffect, useState } from 'react';
import Main from './MainPage/Main';
import Premise from './Premise/Premise';
import Authorization from './AuthorizationPage/Authorization';
import axios from 'axios';


function App() {
    const [isAuth, setAuth] = useState(true);

    useEffect(() => {
        const isAuthenticated = async () => {
            try {
                const accessToken = localStorage.getItem('access_token');
                const refreshToken = localStorage.getItem('refresh_token');
                const response = await axios.post('http://localhost:3001/auth/resource', {
                    accessToken,
                    refreshToken
                });
                setAuth(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        isAuthenticated();
    }, []);
      
    return (
        
        <Router>
                <Routes>
                    <Route path="/login" element={<Authorization/>}/>
                    <Route path="/main" element={isAuth ? <Main/> : <Navigate to="/login" replace />}/>
                    <Route path="/premise?id=${primeseID}" element={isAuth ? <Premise/> : <Navigate to="/login" replace />}/>
                </Routes>
        </Router>
    );
}

export default App;
