import '../../styles/Authorization.css';
import axios from 'axios';
import { useState } from 'react';


function LoginForm() {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            const response = await axios.post('http://localhost:3001/auth/login', {
                login: login,
                password: password
            });
            
            // Получите токены из ответа и сохраните их в куки
            const accessToken = response.data.accessToken;
            const refreshToken = response.data.refreshToken;

            // Пример сохранения токенов в куки
            document.cookie = `access_token=${accessToken}`;
            document.cookie = `refresh_token=${refreshToken}; path=/`;

            // Перенаправление пользователя на другую страницу или выполнение других действий
            // window.location.href = '/profile';
        } catch (error) {
            console.error('Ошибка при входе:', error);
        }
    };

	return (
	    <form onSubmit={handleSubmit} className="authForm">
            <input type="text" name="login" style={{marginTop: '40px'}} placeholder="Логин" />
            <input type="password" name="password" style={{marginTop: '40px'}} placeholder="Пароль" />
            <button type="submit" style={{marginTop: '170px'}}>Войти</button>
        </form>
  	);
}

export default LoginForm;
