import '../../styles/Main.css';
import '../../styles/Owner.css';
import photo from '../../img/my_house.webp';
import axios from 'axios';
import React, { useState, useEffect } from 'react';

function AddPremiseData() {
    const [typePremise, setTypePremise] = useState('Жилые помещение');



    const activeZiliuyTypePremise = {
        backgroundColor: typePremise === 'Жилые помещение' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Жилые помещение' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    const activeSkladTypePremise = {
        backgroundColor: typePremise === 'Склады' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Склады' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    const activeEmptyTypePremise = {
        backgroundColor: typePremise === 'Пустые помещения' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Пустые помещения' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    const activeOfficeTypePremise = {
        backgroundColor: typePremise === 'Офисы' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Офисы' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    const activeComercialTypePremise = {
        backgroundColor: typePremise === 'Комерчиские помещения' ? 'rgb(60, 80, 254)' : 'transparent', // изменение цвета фона
        color: typePremise === 'Комерчиские помещения' ? 'white' : 'black', // изменение цвета текста
        border: 'none', // удаление границы
        borderRadius: '8px',
        cursor: 'pointer', // курсор при наведении
    };

    
    

	return (
        <div className="premiseData">
            <form className='premisePhotoController'>
                <img src={photo} alt="Uploaded"/>
                <input type="file" accept="image/*" className='loadPremisePhoto' />
                <input type="text" className='premiseName' placeholder='Название помещения'/>
                <input type="text" className='premiseName' placeholder='адресс'/>
                <div className='changeOwnerList'>
                    <button style={activeZiliuyTypePremise} onClick={() => setTypePremise("Жилые помещение")}>Мои помещения</button>
					<button style={activeSkladTypePremise}onClick={() => setTypePremise("Склады")}>Арендуемые</button>
					<button style={activeEmptyTypePremise} onClick={() => setTypePremise("Пустые помещения")}>Арендуемые</button>
					<button style={activeOfficeTypePremise} onClick={() => setTypePremise("Офисы")}>Арендуемые</button>
					<button style={activeComercialTypePremise} onClick={() => setTypePremise("Комерчиские помещения")}>Арендуемые</button>
                </div>
            </form> 

            {/* <form className="userForm">
                  <input placeholder='Имя' value={name} onChange={(e) => setName(e.target.value)}/>
                  <input placeholder='Фамилия' value={surname} onChange={(e) => setSurname(e.target.value)}/>
                  <input placeholder='Номер телефона' value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)}/>
                  <button>Стать владельцем</button>
            </form>

            <button className='saveButton' onClick={save}>Сохранить измения</button> */}
        </div>
  	);
}

export default AddPremiseData;
