import '../../styles/Main.css';
import '../../styles/User.css';
import ava from '../../img/ava.png';
import axios from 'axios';
import React, { useState, useEffect } from 'react';

function UserParams() {
      const [errorText, setErrorText] = useState('');
      const [name, setName] = useState('');
      const [surname, setSurname] = useState('');
      const [phoneNumber, setPhoneNumber] = useState('');
      const [login, setLogin] = useState('');
      const [ava, setAva] = useState(null);
      const [ava2, setAva2] = useState(null);


      useEffect(() => {
            const user  = JSON.parse(localStorage.getItem('user'));

            const fetchData = async () => {
                  setUser(user);
      
                  try {
                      const response = await axios.get(`http://localhost:3001/user/photo?login=${user .Login}`, {
                          responseType: 'blob' // Установить responseType на 'blob' для получения данных в виде Blob
                      });
      
                      const reader = new FileReader();
                      reader.readAsDataURL(response.data); // Прочитать данные в виде Data URL
      
                      reader.onload = () => {
                      const photoUrl = reader.result; // Получить Data URL изображения
                      setAva(photoUrl);
                      };
                    } catch (error) {
                      console.error('Error fetching user photo:', error);
                    }
            };
      
              // Загрузка данных с сервера при монтировании компонента
            fetchData();
        
            const setUserValue = async () => {
                  const user = JSON.parse(localStorage.getItem('user'))
                  setName(user.Name);
                  setSurname(user.Surname);
                  setPhoneNumber(user.PhoneNumber);
                  setLogin(user.Login);
                  setAva(null);
            };

            setUserValue();
      }, []);

      const save = async (e) => {
            if(name === '' || surname === '') {
                  setErrorText("Вы не заполнили все поля!"); 
                  return;
            }

            try {
                  const formData = new FormData();
                  formData.append('name', name);
                  formData.append('surname', surname);
                  formData.append('phoneNumber', phoneNumber);
                  console.log(ava);
                  formData.append('photo', ava2);
                  formData.append('login', login);

                  const response = await axios.put('http://localhost:3001/user/edit', formData, {
                        headers: {
                            'Content-Type': 'multipart/form-data'
                        }
                  });

                  const user = response.data.user;
                  console.log(response.data.user);
                  
                  localStorage.setItem('user', JSON.stringify(user));

                  window.location.href = '/main';
            } catch (error) {
                  console.error('Ошибка при изменении данных пользователя:', error);
            }
      };

      const changeAva = (e) => {
            const file = e.target.files[0];
            const reader = new FileReader();
            
            setAva2(file);
            reader.onloadend = () => {
                  setAva(reader.result);
            };
        
            if (file) {
              reader.readAsDataURL(file);
            }
          };

	return (
        <div className="userParams">
            <div className='avaController'>
                  <img src={ava} alt="Uploaded"/>
                  <div className='login'>{login}</div>
                  <input type="file" accept="image/*" onChange={changeAva}/>

            </div>

            <form className="userForm">
                  <input placeholder='Имя' value={name} onChange={(e) => setName(e.target.value)}/>
                  <input placeholder='Фамилия' value={surname} onChange={(e) => setSurname(e.target.value)}/>
                  <input placeholder='Номер телефона' value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)}/>
                  <button>Стать владельцем</button>
            </form>

            <button className='saveButton' onClick={save}>Сохранить измения</button>
        </div>
  	);
}

export default UserParams;
