import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation, Navigate  } from "react-router-dom";
import { useEffect } from 'react';
import Main from './MainPage/Main';
import Premise from './Premise/Premise';
import Authorization from './AuthorizationPage/Authorization';

function App() {
    const isAuthenticated = () => {
        // Здесь вы можете реализовать вашу логику проверки авторизации
        // Например, проверка наличия токена доступа в localStorage или куках
        // Возвращаем true, если пользователь авторизован, и false, если нет
        return true; // Фиктивное значение, замените его на вашу логику
      };
      
      // Приватный маршрут, который требует авторизации
      const PrivateRoute = ({ element: Element, ...rest }) => {
        return isAuthenticated() ? <Element {...rest} /> : <Navigate to="/login" replace />;
      };

      
    return (
        
        <Router>
                <Routes>
                    <Route path="/login" element={<PrivateRoute element={<Main />} />}/>
                    <Route path="/main" element={<Main/>}/>
                    <Route path="/premise" element={<Premise/>}/>
                </Routes>
        </Router>
    );
}

export default App;
