import '../../styles/Main.css';
import '../../styles/Rentals.css';
import deleteButton from '../../img/delete.svg';



function OwnershipList({ premises }) {
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
      };
    
      const handleMouseLeave = () => {
        setIsHovered(false);
      };

	return (
        

        <div className="userList">
            <a href='/add_premise' className='addPremiseButton'><p>+</p></a>

            {Array.isArray(premises) && premises.map((premise) => (
                <a href={`/premise/${premise.ID}`} className="rental" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                    <img src={premise.Image}/>
                    <div className="name_price">
                        <div>{premise.Name}</div>
                        <div style={{marginTop: '25px'}}>{premise.Price} BYN</div>
                    </div>
                    {isHovered && (
                        <button className='deleteButton'><img src={deleteButton}/></button>
                    )}
                </a>
            ))}
            
        </div>
  	);
}

export default OwnershipList;
