import '../../styles/Authorization.css';
import myRent from '../../img/my_rent.svg';
import message from '../../img/message.svg';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SelectAccountController  from './SelectAccountController';



function UserInterface() {
    const [user, setUser] = useState({});
    const [ava, setAva] = useState(null);
    const [isVisibleUserController, setIsVisibleUserController] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            const user  = JSON.parse(localStorage.getItem('user'));
            setUser(user);

            try {
                const response = await axios.get(`http://localhost:3001/user/photo?login=${user .Login}`, {
                    responseType: 'blob' // Установить responseType на 'blob' для получения данных в виде Blob
                });

                const reader = new FileReader();
                console.log(response.data);
                reader.readAsDataURL(response.data); // Прочитать данные в виде Data URL

                reader.onload = () => {
                const photoUrl = reader.result; // Получить Data URL изображения
                setAva(photoUrl);
                };
              } catch (error) {
                console.error('Error fetching user photo:', error);
              }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);



    const handleUser = () => {
        setIsVisibleUserController(!isVisibleUserController);
    };

	return (
	    <div class="userInterface">
            <a href="/rental" className="userInterfaceComponent">
                <img src={myRent} alt=""/>
                <p>Аренды</p>
            </a>
    
            <a href="/" className="userInterfaceComponent">
                <img  src={message} alt=""/>
                <p>Сообщения</p>
            </a>
    
            <div className="userInterfaceComponent" onClick={handleUser}>
                <img src={ava} alt=""/>
                <p>{user.Login}</p>
            </div>

            {isVisibleUserController && <SelectAccountController/>}
            
        </div>
  	);
}

export default UserInterface;
