import '../../styles/Main.css';
import '../../styles/Rentals.css';
import noPhoto from '../../img/no-photo.png';
import deleteButton from '../../img/delete.svg';
import axios from 'axios';
import { useState, useEffect } from 'react';


function UserList({ users }) {
    const [usersList, setUsersList] = useState([]);

    useEffect(() => {
        setUsersList(users);
    }, [users]);

    const deletePremise = async (id) => {
        try {
            console.log(id);
            // Отправляем запрос на удаление объекта на сервер
            await axios.delete(`http://localhost:3001/user/remove`, {
                data: {id: id}
            });

            users = (usersList.filter(user => user.ID !== id));
            setUsersList(users);
            // Обновляем список помещений, удаляя удаленный объект
        } catch (error) {
            console.error("Ошибка при удалении помещения:", error);
        }
    };

	return (
        <div className="userList">
            <a href='/add_user' className='addPremiseButton'><p>+</p></a>

            {Array.isArray(usersList) && usersList.map((user) => (
                <a href={`/edit_user/${user.ID}`} className="rental">
                {user.Photo !== 'data:image/png;base64,null' ? ( <img src={user.Photo} alt=""/>) : (<img src={noPhoto} alt="Placeholder"/>)}
                    <div className="name_price">
                        <div>{user.Login}</div>
                    </div>
                    <button className='deleteButton' onClick={(e) => { e.preventDefault(); deletePremise(user.ID); }}><img src={deleteButton}/></button>
                </a>
            ))}

        </div>
  	);
}

export default UserList;
