import '../../styles/Authorization.css';
import myRent from '../../img/my_rent.svg';
import message from '../../img/message.svg';
import React, { useState, useEffect } from 'react';
import axios from 'axios';


function UserInterface() {
    const [user, setUser] = useState({});
    const [ava, setAva] = useState(null);
    const [isVisibleUserController, setIsVisibleUserController] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            setUser(JSON.parse(localStorage.getItem('user')));

            try {
                const response = await axios.get(`http://localhost:3001/user/photo?login=Ilya`, {
                  responseType: 'arraybuffer' // Установить responseType на 'arraybuffer' для получения бинарных данных
                });
        
                console.log(response.data);
                // Преобразовать полученные бинарные данные в URL изображения
                const photoUrl = `data:image/png;base64,${Buffer.from(response.data, 'binary').toString('base64')}`;
                setAva(photoUrl);
              } catch (error) {
                console.error('Error fetching user photo:', error);
              }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);


    const handleLogout = () => {
        // Очистить данные из локального хранилища или выполнить другие действия при выходе
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');

        window.location.href = '/login';
    };

    const handleUser = () => {
        setIsVisibleUserController(!isVisibleUserController);
    };

	return (
	    <div class="userInterface">
            <a href="/rental" className="userInterfaceComponent">
                <img src={myRent} alt=""/>
                <p>Аренды</p>
            </a>
    
            <a href="/" className="userInterfaceComponent">
                <img  src={message} alt=""/>
                <p>Сообщения</p>
            </a>
    
            <div className="userInterfaceComponent" onClick={handleUser}>
                <img src={ava} alt=""/>
                <p>{user.Login}</p>
            </div>

            {isVisibleUserController && <div className="selectAccountController">
                <a href='/user'><p>Настроить</p></a>
                <button onClick={handleLogout}>Выйти</button>
            </div>}
            
        </div>
  	);
}

export default UserInterface;
