import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation, Navigate  } from "react-router-dom";
import { useEffect, useState } from 'react';
import Main from './MainPage/Main';
import OwnerMain from './OwnerMainPage/OwnerMain';
import AddPremise from './OwnerMainPage/AddPemise';
import EditPemise from './OwnerMainPage/EditPemise';
import Premise from './Premise/Premise';
import ApplicationList from './RentalPage/ApplicationList';
import Authorization from './AuthorizationPage/Authorization';
import Rental from './RentalPage/Rental';
import User from './UserPage/User';
import axios from 'axios';


function App() {
    const [isAuth, setAuth] = useState(true);
    const [role, setRole] = useState();

    useEffect(() => {
        const isAuthenticated = async () => {
            try {
                const accessToken = localStorage.getItem('access_token');
                const refreshToken = localStorage.getItem('refresh_token');
                const response = await axios.post('http://localhost:3001/auth/resource', {
                    accessToken,
                    refreshToken
                });
                setAuth(response.data);
                setRole(JSON.parse(localStorage.getItem('user')).Role)
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        isAuthenticated();
    }, []);
      
    return (
        
        <Router>
                <Routes>
                    
                    <Route path="/login" element={<Authorization/>}/>
                    <Route path="/user" element={isAuth ? <User/> : <Navigate to="/login" replace />}/>
                    {role === 0 && (
                        <>
                            <Route path="/main" element={isAuth ? <Main/> : <Navigate to="/login" replace />}/>
                            <Route path="/premise/:id" element={isAuth ? <Premise/> : <Navigate to="/login" replace />}/>
                            <Route path="/rental" element={isAuth ? <Rental/> : <Navigate to="/login" replace />}/>
                        </>
                    )}
                    {role === 1 && (
                        <>
                            <Route path="/main" element={isAuth ? <OwnerMain/> : <Navigate to="/login" replace />}/>
                            <Route path="/application" element={isAuth ? <ApplicationList/> : <Navigate to="/login" replace />}/>
                            <Route path="/add_premise" element={isAuth ? <AddPremise/> : <Navigate to="/login" replace />}/>
                            <Route path="/edit/:id" element={isAuth ? <EditPemise/> : <Navigate to="/login" replace />}/>
                        </>
                    )}
                    {role === 1 && <Route path="/main" element={isAuth ? <OwnerMain/> : <Navigate to="/login" replace />}/>}
                </Routes>
        </Router>
    );
}

export default App;
