import '../../styles/Authorization.css';
import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';



function LoginForm() {
    const navigate  = useNavigate();
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            console.log(login);
            const response = await axios.post('http://localhost:3001/auth/login', {
                login: login,
                password: password
            });
            const accessToken = response.data.accessToken;
            const refreshToken = response.data.refreshToken;
            console
            localStorage.setItem('access_token', accessToken);
            localStorage.setItem('refresh_token', refreshToken);

            console.log(response);
            navigate('/main');
            // Перенаправление пользователя на другую страницу или выполнение других действий
        } catch (error) {
            console.error('Ошибка при входе:', error);
        }
    };

	return (
	    <form onSubmit={handleSubmit} className="authForm">
            <input type="text" name="login" style={{marginTop: '40px'}} placeholder="Логин" value={login} onChange={(e) => setLogin(e.target.value)}/>
            <input type="password" name="password" style={{marginTop: '40px'}} placeholder="Пароль" value={password} onChange={(e) => setPassword(e.target.value)}/>
            <button type="submit" style={{marginTop: '170px'}}>Войти</button>
        </form>
  	);
}

export default LoginForm;
