    //         } catch (error) {
    //             console.error('Error fetching data:', error);
    //         }
    //     };

    //     isAuthenticated();
    // }, []);

    const isAuthenticated = async () => {
        try {
            const accessToken = localStorage.getItem('access_token');
            const refreshToken = localStorage.getItem('refresh_token');
            console.log(accessToken);
            const response = await axios.post('http://localhost:3001/auth/resource', {
                accessToken,
                refreshToken
            });
            console.log(response.data);
            return(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };
      
      // Приватный маршрут, который требует авторизации
    //   const PrivateRoute = (Component) => {
    //     return isAuthenticated() ? <Component /> : <Navigate to="/login" replace />;
    //   };

      
    return (
        
        <Router>
                <Routes>
                    <Route path="/login" element={<Authorization/>}/>
                    <Route path="/main" element={isAuthenticated() ? <Main/> : <Navigate to="/login" replace />}/>
                    <Route path="/premise" element={isAuthenticated() ? <Premise/> : <Navigate to="/login" replace />}/>
                </Routes>
        </Router>
    );
}

export default App;
