import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation, Navigate  } from "react-router-dom";
import { useEffect, useState } from 'react';
import Main from './MainPage/Main';
import Premise from './Premise/Premise';
import Authorization from './AuthorizationPage/Authorization';
import axios from 'axios';


function App() {
    const [isAuth, setAuth] = useState(false);

    const isAuthenticated = async () => {
        await axios.get('http://localhost:3001/auth/resource')
        .then(response => {
            console.log(response.data);
            if(response.data == 'true') setAuth(true);
            else setAuth(false);
        })
        .catch(error => {
            console.log('bbbb');
            console.error('Error fetching data:', error);
        });

        return isAuth
    };
      
      // Приватный маршрут, который требует авторизации
    //   const PrivateRoute = (Component) => {
    //     return isAuthenticated() ? <Component /> : <Navigate to="/login" replace />;
    //   };

      
    return (
        
        <Router>
                <Routes>
                    <Route path="/login" element={<Authorization/>}/>
                    <Route path="/main" element={isAuthenticated() ? <Main/> : <Navigate to="/login" replace />}/>
                    <Route path="/premise" element={isAuthenticated() ? <Premise/> : <Navigate to="/login" replace />}/>
                </Routes>
        </Router>
    );
}

export default App;
