import '../../styles/Main.css';
import '../../styles/Owner.css';
import noPhoto from '../../img/no-photo.png';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function EditUserData() {
    const location = useLocation();
    const segments = location.pathname.split('/');
    const id = segments[segments.length - 1];

    const [errorText, setErrorText] = useState('');
    const [photo, setPhoto] = useState(noPhoto);
    const [photo2, setPhoto2] = useState(null);
    const [role, setRole] = useState(0);
    const [login, setLogin] = useState('');
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    
   
    useEffect(() => {

        const fetchData = async () => {
            const location = useLocation();
            const segments = location.pathname.split('/');
            const id = segments[segments.length - 1];
            try {
                const response = await axios.get(`http://localhost:3001/user/person?id=${id}`);
                console.log(response.data);
                if(response.data.Photo === 'null') {response.data.Photo = noPhoto}
                const user = response.data;
                

                setPhoto(user.Photo);
                setName(user.Name);
                setLogin(user.Login);
                setName(user.Name);
                setSurname(user.Surname);
                setPhoneNumber(user.PhoneNumber);
                setRole(user.Role);
            } catch (error) {
                console.error('Ошибка загрузки данных:', error);
            }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);

    const add = async (e) => {
        if(login === '' || name === '' || surname === '') {
            setErrorText("Название, адресс и цена должны быть заполнены!"); 
            return;
        }
        
        console.log('ракета');
        try {
                //получить id из url
              const formData = new FormData();
              formData.append('id', id);
              formData.append('name', name);
              formData.append('surname', surname);
              formData.append('login', login);
              formData.append('role', role);
              formData.append('photo', photo2);

              console.log(formData);
              const response = await axios.post('http://localhost:3001/user/edit', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
              });

              console.log(response.data.user);
              

              window.location.href = '/main';
        } catch (error) {
              console.error('Ошибка при изменении данных пользователя:', error);
        }
  } ;

   
    const changePhoto = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        
        setPhoto2(file);
        reader.onloadend = () => {
              setPhoto(reader.result);
        };
    
        if (file) {
          reader.readAsDataURL(file);
        }
    };
    

    return (
        <div className="premiseData">
            <form className='premisePhotoController'>
                <img src={photo} alt="Uploaded"/>
                <input type="file" accept="image/*" className='loadPremisePhoto' onChange={changePhoto}/>
                <input type="text" className='premiseName' placeholder='Логин' value={login} onChange={(e) => setLogin(e.target.value)}/>
                <input type="text" className='premiseName' placeholder='Имя' value={name} onChange={(e) => setName(e.target.value)}/>
                <input type="text" className='premiseName' placeholder='Фамилия' value={surname} onChange={(e) => setSurname(e.target.value)}/>
                <input type="text" className='premiseName' placeholder='Номер телефона' value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)}/>
            </form> 
            
            <p id="errorMessage" style={{marginTop: '15px'}}>{errorText}</p>
            <button className='addButton' onClick={add}>Добавить</button>
        </div>
    );
}

export default EditUserData;
