import '../../styles/Main.css';
import Header from '../MainPage/Header';
import AnnouncementList from './AnnouncementList';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
const accessToken = localStorage.getItem("accessToken");
const refreshToken = localStorage.getItem("refreshToken");

function Announcement() {
    const [announcement, setUsers] = useState([]);
    const ws = new WebSocket('wss://localhost:3441');

	useEffect(() => {
        const fetchData = async () => {
            try {
                // Обработчик события открытия соединения
                ws.addEventListener('open', function (event) {
                    console.log('WebSocket соединение установлено');
                    
                    // Отправляем сообщение на сервер после установления соединения
                    ws.send('Привет, сервер!');
                });
                
                // Обработчик события получения сообщения от сервера
                ws.addEventListener('message', function (event) {
                    console.log('Получено сообщение от сервера:', event.data);
                });
                
                // Обработчик события закрытия соединения
                ws.addEventListener('close', function (event) {
                    console.log('WebSocket соединение закрыто');
                });
                
                // Обработчик события ошибки
                ws.addEventListener('error', function (event) {
                    console.error('Произошла ошибка:', event);
                });
            } catch (error) {
                console.error('Ошибка загрузки данных:', error);
            }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);

	return (
		<div>
			<div>
				<Header/>
				<AnnouncementList users={users}/>
			</div>
		</div>
  	);
}

export default Announcement;
