import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation, Navigate  } from "react-router-dom";
import { useEffect, useState } from 'react';
import Main from './MainPage/Main';
import OwnerMain from './OwnerMainPage/OwnerMain';
import AddPremise from './OwnerMainPage/AddPemise';
import EditPemise from './OwnerMainPage/EditPemise';
import Premise from './Premise/Premise';
import ApplicationList from './RentalPage/ApplicationList';
import Authorization from './AuthorizationPage/Authorization';
import Rental from './RentalPage/Rental';
import User from './UserPage/User';
import Admin from './AdminPage/Admin';
import AddUser from './AdminPage/AddUser';
import EditUser from './AdminPage/EditUser';
import axios from 'axios';


function App() {
    const [auth, setAuth] = useState({});

    useEffect(() => {
        const isAuthenticated = async () => {
            try {
                const response = await axios.get('http://localhost:3001/auth/resource');
                setAuth(response.data);
            } catch (error) {
                if(error.response.status === 401) {
                    setAuth(undefined); 
                    return;
                }
                console.error('Error fetching data:', error);
            }
        };

        isAuthenticated();
    }, []);
      
    return (
        
        <Router>
                <Routes>
                    
                    <Route path="/login" element={<Authorization/>}/>
                    <Route path="/user" element={auth ? <User/> : <Navigate to="/login" replace />}/>
                    {/* {auth.role === 0 && (
                        <>
                            <Route path="/main" element={auth ? <Main/> : <Navigate to="/login" replace />}/>
                            <Route path="/premise/:id" element={auth ? <Premise/> : <Navigate to="/login" replace />}/>
                            <Route path="/rental" element={auth ? <Rental/> : <Navigate to="/login" replace />}/>
                        </>
                    )}
                    {auth.role === 1 && (
                        <>
                            <Route path="/main" element={auth ? <OwnerMain/> : <Navigate to="/login" replace />}/>
                            <Route path="/application" element={auth ? <ApplicationList/> : <Navigate to="/login" replace />}/>
                            <Route path="/add_premise" element={auth ? <AddPremise/> : <Navigate to="/login" replace />}/>
                            <Route path="/edit_premise/:id" element={auth ? <EditPemise/> : <Navigate to="/login" replace />}/>
                        </>
                    )}
                    {auth.role === 2 && (
                        <>
                            <Route path="/main" element={auth ? <Admin/> : <Navigate to="/login" replace />}/>
                            <Route path="/add_user" element={auth ? <AddUser/> : <Navigate to="/login" replace />}/>
                            <Route path="/edit_user/:id" element={auth ? <EditUser/> : <Navigate to="/login" replace />}/>
                        </>
                    )} */}
                </Routes>
        </Router>
    );
}

export default App;
