import '../../styles/Main.css';
import '../../styles/Rentals.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import noPhoto from '../../img/no-photo.png';

function ApplicationList() {
    const [applications, setApplications] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const user  = JSON.parse(localStorage.getItem('user'));
                const response = await axios.get(`http://localhost:3001/rental/applications?id=${user.ID}`);
                console.log(response.data);
                setApplications(response.data);
            } catch (error) {
                console.error('Ошибка загрузки данных:', error);
            }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);

	return (
        <div className="userList">
            {Array.isArray(applications) && applications.map((application) => (
                <a href={`/premise/${application.Premise.ID}`} className="rental">
                    <img src={application.Premise.Image}/>
                    <div className="name_price">
                        <div>
                            <p>{application.Tenant.Login}</p>
                        </div>
                        <div>{application.Premise.Name}</div>
                        <div style={{marginTop: '25px'}}>{application.Premise.Price} BYN</div>
                    </div>
                    <div className='rentalDate'>
                        {new Date(application.StartDate).toLocaleDateString()}
                    </div>

                </a>
            ))}
            
        </div>
  	);
}

export default ApplicationList;
