import '../../styles/Main.css';
import '../../styles/User.css';
import ava from '../../img/ava.png';
import React, { useState, useEffect } from 'react';

function UserParams({ user }) {
      const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if(login === '' || password === '') {
            setErrorText("Вы не заполнили все поля!"); 
            return;
        }

        try {
            console.log(login);
            const response = await axios.post('http://localhost:3001/auth/login', {
                login: login,
                password: password
            });
            const accessToken = response.data.accessToken;
            const refreshToken = response.data.refreshToken;
            const user = response.data.user;
            console.log(user);
            
            localStorage.setItem('access_token', accessToken);
            localStorage.setItem('refresh_token', refreshToken);
            localStorage.setItem('user', JSON.stringify(user));

            window.location.href = '/main';
        } catch (error) {
            if(error.response.status === 401) {
                setErrorText("Неверный логин или пароль!"); 
                return;
            }
            console.error('Ошибка при входе:', error);
        }
    };
    
	return (
        <div className="userParams">
            <div className='avaController'>
                  <img src={ava}/>
                  <div className='login'>{user.Login}</div>
                  <button>Загрузить фото</button>
            </div>

            <form className="userForm">
                  <input placeholder='Имя' value={user.Name}/>
                  <input placeholder='Фамилия' value={user.Surname}/>
                  <input placeholder='Номер телефона'/>
                  <button>Стать владельцем</button>
            </form>

            <button className='saveButton'>Сохранить измения</button>
        </div>
  	);
}

export default UserParams;
