import '../../styles/Main.css';
import '../../styles/Authorization.css';
import noPhoto from '../../img/no-photo.png';
import deleteButton from '../../img/delete.svg';
import axios from 'axios';
import { useState, useEffect } from 'react';
const accessToken = localStorage.getItem("accessToken");
const refreshToken = localStorage.getItem("refreshToken");

function AnnouncementList({ announcements }) {
    const [announcementsList, setAnnouncementsListList] = useState([]);

    useEffect(() => {
        setAnnouncementsListList(announcements);
    }, [announcements]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if(login === '' || password === '') {
            setErrorText("Вы не заполнили все поля!"); 
            return;
        }

        try {
            console.log(login);
            const response = await axios.post('https://localhost:3441/auth/login', {
                login: login,
                password: password
            });
            const accessToken = response.data.accessToken;
            const refreshToken = response.data.refreshToken;
            console.log(accessToken);
            localStorage.setItem('accessToken', accessToken);
            localStorage.setItem('refreshToken', refreshToken);

            window.location.href = '/main'; 
        } catch (error) {
            if(error.response.status === 401) {
                setErrorText("Неверный логин или пароль!"); 
                return;
            }
            console.error('Ошибка при входе:', error);
        }
    };

	return (
        <div className="userList" style={{paddingTop: '40px'}}>
            <form submitclassName="announcementForm" >
                <input type="text" name="announcement" placeholder="Объявление"/>
                <button type="submit" >Отправить</button>
            </form>

            <div style={{paddingTop: '30px'}}>
                {Array.isArray(announcementsList) && announcementsList.map((announcement) => (
                    <div className="rental">
                        <div className="name_price">
                            <div>{announcements}</div>
                        </div>
                    </div>
                ))}
            </div>


        </div>
  	);
}

export default AnnouncementList;
