import '../../styles/Premise.css';
import PremiseInfo from './PremiseInfo';
import RentInfo from './RentInfo';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { useState, useEffect } from 'react';

function PremiseElement() {
	const [primese, setPrimese] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
				const location = useLocation();
				const segments = location.pathname.split('/');
				const id = segments[segments.length - 1];

                const response = await axios.get(`http://localhost:3001/premise/notrenated_element?id=${id}`);
                setPrimese(response.data);
				console.log(primese);
            } catch (error) {
                console.error('Ошибка загрузки данных:', error);
            }
        };

        // Загрузка данных с сервера при монтировании компонента
        fetchData();
    }, []);

	return (
	    <div className="premiseElement">
			<PremiseInfo data={primese}/>
			<RentInfo data={primese}/>
        </div>
  	);
}

export default PremiseElement;
